--
-- Table structure for table `question`
--

DROP TABLE IF EXISTS `question`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `question` (
  `question_id` int NOT NULL AUTO_INCREMENT,
  `category_id` int NOT NULL,
  `time` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `language_id` int NOT NULL DEFAULT '1',
  `user_id` int NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `content` text COLLATE utf8mb4_unicode_ci,
  `public` tinyint(1) DEFAULT '1',
  `last_editor_id` int DEFAULT NULL,
  `last_edited_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`question_id`),
  KEY `last_editor_id` (`last_editor_id`),
  KEY `idx_category_id` (`category_id`),
  KEY `idx_language_id` (`language_id`),
  KEY `idx_user_id` (`user_id`),
  KEY `idx_question_time` (`time`),
  KEY `idx_question_public` (`public`),
  CONSTRAINT `question_ibfk_1` FOREIGN KEY (`category_id`) REFERENCES `category` (`id`) ON DELETE CASCADE,
  CONSTRAINT `question_ibfk_2` FOREIGN KEY (`language_id`) REFERENCES `language` (`id`),
  CONSTRAINT `question_ibfk_3` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`),
  CONSTRAINT `question_ibfk_4` FOREIGN KEY (`last_editor_id`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=186 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

